# Finding a Motif in DNA: https://rosalind.info/problems/subs/

sample_string = ''
motif = 'GGCTCGGGG'

def finding_motif_in_DNA(sample_string: str, motif: str):
  try: 
    return " ".join([str(i+1) for i in range(0, len(sample_string)-len(motif)+1) if motif == sample_string[i:i+len(motif)]])
      
  except Exception as error: 
    return error

print(finding_motif_in_DNA(sample_string, motif))
