#2 Transcribing DNA into RNA: https://rosalind.info/problems/rna/

sample_string = 'GATGGAACTTGACTACGTAAATT'
# sample_string = ""

def transcribing_dna_to_rna(string: str):
  """
    Basically replacing T with Uracil
  """
  try: return string.replace("T", "U")
  except Exception as error: return error

print(transcribing_dna_to_rna(sample_string))
print("Checking with the result from website:", transcribing_dna_to_rna(sample_string)=="GAUGGAACUUGACUACGUAAAUU")